import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Col, Row } from 'react-bootstrap';
import getUnsignedTxn from '../APIs/API';

function ProjectForm() {

    const handleSubmit = async (event) => {
        console.log("hello")
        // Stop the form from submitting and refreshing the page.
        event.preventDefault()

        // Get data from the form.
        const data = {
            name: event.target.creator.value,
            goal: event.target.goal.value,
            start: event.target.duration.value,
        }
        let txn = await getUnsignedTxn(data.name, data.goal, data.start);
        console.log(txn);
    }



    return (
        <Form>
            <Row />
            <Row>
                <Col />
                <Col>
                    <Form.Group className="mb-2" controlId="creator">
                        <Form.Control placeholder="Enter creator" />
                    </Form.Group>

                    <Form.Group className="mb-2" controlId="goal">
                        <Form.Control placeholder="Enter goal" />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="duration">
                        <Form.Control placeholder="Duration" />
                    </Form.Group>
                    <Button 
                    onClick={handleSubmit}
                    variant="primary" 
                    type="submit">
                        Submit
                    </Button>
                </Col>
                <Col />
            </Row>
            <Row />
        </Form>
    );
}

export default ProjectForm;
