import './App.css';
import ProjectForm from './Components/ProjectForm';
import MyNavbar from './Components/MyNavbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Col, Button } from 'react-bootstrap';


function App() {

  const connectAlgoSigner = async () => {
    if (typeof AlgoSigner !== "undefined" && typeof window !== 'undefined') {
      await AlgoSigner.connect();
      const account = await getUserAccount();
      localStorage.setItem('connectedAccount', String(account));
      if (account) setIsAccountConnected(true)
      addToast("Account connected successfully", {
        appearance: 'success',
        autoDismiss: true,
      })
    } else {
      addToast("This browser doesn't have the AlgoSigner extension installed", {
        appearance: 'warning',
        autoDismiss: true,
      })
    }
  };


  return (
    <div className="App">

      <MyNavbar/>
      <Col/>
      <Col>
        <ProjectForm/>
      </Col>
      <Col/>
      <Button
        onClick={connectAlgoSigner}
        />


    </div>
  );
}

export default App;
