import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Col, Row } from 'react-bootstrap';
import getUnsignedTxn from '../APIs/API';

function ProjectForm() {

    const handleSubmit = async (event) => {
        // Stop the form from submitting and refreshing the page.
        event.preventDefault()

        // Get data from the form.
        const data = {
            creator: event.target.creator.value,
            goal: event.target.goal.value,
            start: event.target.duration.value,
        }
        try {

            let txn = await getUnsignedTxn("R3Z6A6BUXWRYZ3IFBSK7Y54EBN6FRBSYGS4GNTNE2DB5GXJAC64JOMNFNI", data.goal, data.start);

            let txn_uint8 = new Uint8Array();

            for(let i = 0; i < txn.length; i++) {
                txn_uint8[i] = txn[i];
            }

            console.log(txn_uint8[0]);


            return

            /*global AlgoSigner*/
            // Use the AlgoSigner encoding library to make the transactions base64
            const txn_b64 = await AlgoSigner.encoding.msgpackToBase64(txn);

            // Sign the transaction
            let signedTxs = await AlgoSigner.signTxn([{ txn: txn_b64 }]);
            console.log(signedTxs);

            // Get the base64 encoded signed transaction and convert it to binary
            let binarySignedTx = await AlgoSigner.encoding.base64ToMsgpack(
                signedTxs[0].blob
            );

            console.log(binarySignedTx)
        } catch (err) {
            console.log(err)
        }

    }



    return (
        <Form onSubmit={handleSubmit}>
            <Row />
            <Row>
                <Col />
                <Col>
                    <Form.Group className="mb-2" controlId="creator">
                        <Form.Control placeholder="Enter creator" />
                    </Form.Group>

                    <Form.Group className="mb-2" controlId="goal">
                        <Form.Control placeholder="Enter goal" />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="duration">
                        <Form.Control placeholder="Duration" />
                    </Form.Group>
                    <Button
                        variant="primary"
                        type="submit">
                        Submit
                    </Button>
                </Col>
                <Col />
            </Row>
            <Row />
        </Form>
    );
}

export default ProjectForm;
